        //查询 student 下 wangwu 的分数
        Double score = redisTemplate.opsForZSet().score("student", "wangwu");
        //查询 一个元素在集合中的排名  排名从0开始
        Long rank = redisTemplate.opsForZSet().rank("student", "zhangshan");
