        &#x2F;&#x2F;查询 student 下 wangwu 的分数
        Double score &#x3D; redisTemplate.opsForZSet().score(&quot;student&quot;, &quot;wangwu&quot;);
        &#x2F;&#x2F;查询 一个元素在集合中的排名  排名从0开始
        Long rank &#x3D; redisTemplate.opsForZSet().rank(&quot;student&quot;, &quot;zhangshan&quot;);
        &#x2F;&#x2F; 查询前三个元素
        Set&lt;String&gt; students &#x3D; redisTemplate.opsForZSet().range(&quot;student&quot;, 0, 2);
        &#x2F;&#x2F; 查询  前三个元素的键值对
        Set&lt;ZSetOperations.TypedTuple&lt;String&gt;&gt; student &#x3D; redisTemplate.opsForZSet().rangeWithScores(&quot;student&quot;, 0, 2);
        &#x2F;&#x2F; 查询 根据值的区间查询 key
        Set&lt;String&gt; students1 &#x3D; redisTemplate.opsForZSet().rangeByScore(&quot;student&quot;, 50, 100);
        &#x2F;&#x2F; 查询 根据值区间查询 Set集合
        Set&lt;ZSetOperations.TypedTuple&lt;String&gt;&gt; student1 &#x3D; redisTemplate.opsForZSet().rangeByScoreWithScores(&quot;student&quot;, 50, 100);
        &#x2F;&#x2F;查询 student 中有几个 key
        Long result &#x3D; redisTemplate.opsForZSet().zCard(&quot;student&quot;);

        &#x2F;&#x2F;根据key-value删除 value允许传入多个
        redisTemplate.opsForZSet().remove(&quot;student&quot;,&quot;zhangsan&quot;,&quot;lisi&quot;);
        &#x2F;&#x2F;根据排名区间删除
        redisTemplate.opsForZSet().removeRange(&quot;student&quot;,0,1);
        &#x2F;&#x2F;根据分数区间删除
        redisTemplate.opsForZSet().removeRangeByScore(&quot;student&quot;,70,90);
    &#125;


增删改查工具类package com.lsqingfeng.springboot.utils; 
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.data.redis.core.RedisTemplate;
import org.springframework.stereotype.Component;

import java.util.List;
import java.util.Map;
import java.util.Set;
import java.util.concurrent.TimeUnit;

&#x2F;**
 * @className: RedisUtil
 * @description:
 * @author: sh.Liu
 * @date: 2022-03-09 14:07
 *&#x2F;
@Component
public class RedisUtil &#123;

    @Autowired
    private RedisTemplate redisTemplate;
    &#x2F;**
     * 给一个指定的 key 值附加过期时间
     *
     * @param key
     * @param time
     * @return
     *&#x2F;
    public boolean expire(String key, long time) &#123;
        return redisTemplate.expire(key, time, TimeUnit.SECONDS);
    &#125;
    &#x2F;**
     * 根据key 获取过期时间
     *
     * @param key
     * @return
     *&#x2F;
    public long getTime(String key) &#123;
        return redisTemplate.getExpire(key, TimeUnit.SECONDS);
    &#125;
    &#x2F;**
     * 根据key 获取过期时间
     *
     * @param key
     * @return
     *&#x2F;
    public boolean hasKey(String key) &#123;
        return redisTemplate.hasKey(key);
    &#125;
    &#x2F;**
     * 移除指定key 的过期时间
     *
     * @param key
     * @return
     *&#x2F;
    public boolean persist(String key) &#123;
        return redisTemplate.boundValueOps(key).persist();
    &#125;

    &#x2F;&#x2F;- - - - - - - - - - - - - - - - - - - - -  String类型 - - - - - - - - - - - - - - - - - - - -

    &#x2F;**
     * 根据key获取值
     *
     * @param key 键
     * @return 值
     *&#x2F;
    public Object get(String key) &#123;
        return key &#x3D;&#x3D; null ? null : redisTemplate.opsForValue().get(key);
    &#125;

    &#x2F;**
     * 将值放入缓存
     *
     * @param key   键
     * @param value 值
     * @return true成功 false 失败
     *&#x2F;
    public void set(String key, String value) &#123;
        redisTemplate.opsForValue().set(key, value);
    &#125;

    &#x2F;**
     * 将值放入缓存并设置时间
     *
     * @param key   键
     * @param value 值
     * @param time  时间(秒) -1为无期限
     * @return true成功 false 失败
     *&#x2F;
    public void set(String key, String value, long time) &#123;
        if (time &gt; 0) &#123;
            redisTemplate.opsForValue().set(key, value, time, TimeUnit.SECONDS);
        &#125; else &#123;
            redisTemplate.opsForValue().set(key, value);
        &#125;
    &#125;

    &#x2F;**
     * 批量添加 key (重复的键会覆盖)
     *
     * @param keyAndValue
     *&#x2F;
    public void batchSet(Map&lt;String, String&gt; keyAndValue) &#123;
        redisTemplate.opsForValue().multiSet(keyAndValue);
    &#125;

    &#x2F;**
     * 批量添加 key-value 只有在键不存在时,才添加
     * map 中只要有一个key存在,则全部不添加
     *
     * @param keyAndValue
     *&#x2F;
    public void batchSetIfAbsent(Map&lt;String, String&gt; keyAndValue) &#123;
        redisTemplate.opsForValue().multiSetIfAbsent(keyAndValue);
    &#125;

    &#x2F;**
     * 对一个 key-value 的值进行加减操作,
     * 如果该 key 不存在 将创建一个key 并赋值该 number
     * 如果 key 存在,但 value 不是长整型 ,将报错
     *
     * @param key
     * @param number
     *&#x2F;
    public Long increment(String key, long number) &#123;
        return redisTemplate.opsForValue().increment(key, number);
    &#125;

    &#x2F;**
     * 对一个 key-value 的值进行加减操作,
     * 如果该 key 不存在 将创建一个key 并赋值该 number
     * 如果 key 存在,但 value 不是 纯数字 ,将报错
     *
     * @param key
     * @param number
     *&#x2F;
    public Double increment(String key, double number) &#123;
        return redisTemplate.opsForValue().increment(key, number);
    &#125;

    &#x2F;&#x2F;- - - - - - - - - - - - - - - - - - - - -  set类型 - - - - - - - - - - - - - - - - - - - -

    &#x2F;**
     * 将数据放入set缓存
     *
     * @param key 键
     * @return
     *&#x2F;
    public void sSet(String key, String value) &#123;
        redisTemplate.opsForSet().add(key, value);
    &#125;

    &#x2F;**
     * 获取变量中的值
     *
     * @param key 键
     * @return
     *&#x2F;
    public Set&lt;Object&gt; members(String key) &#123;
        return redisTemplate.opsForSet().members(key);
    &#125;

    &#x2F;**
     * 随机获取变量中指定个数的元素
     *
     * @param key   键
     * @param count 值
     * @return
     *&#x2F;
    public void randomMembers(String key, long count) &#123;
        redisTemplate.opsForSet().randomMembers(key, count);
    &#125;

    &#x2F;**
     * 随机获取变量中的元素
     *
     * @param key 键
     * @return
     *&#x2F;
    public Object randomMember(String key) &#123;
        return redisTemplate.opsForSet().randomMember(key);
    &#125;

    &#x2F;**
     * 弹出变量中的元素
     *
     * @param key 键
     * @return
     *&#x2F;
    public Object pop(String key) &#123;
        return redisTemplate.opsForSet().pop(&quot;setValue&quot;);
    &#125;

    &#x2F;**
     * 获取变量中值的长度
     *
     * @param key 键
     * @return
     *&#x2F;
    public long size(String key) &#123;
        return redisTemplate.opsForSet().size(key);
    &#125;

    &#x2F;**
     * 根据value从一个set中查询,是否存在
     *
     * @param key   键
     * @param value 值
     * @return true 存在 false不存在
     *&#x2F;
    public boolean sHasKey(String key, Object value) &#123;
        return redisTemplate.opsForSet().isMember(key, value);
    &#125;

    &#x2F;**
     * 检查给定的元素是否在变量中。
     *
     * @param key 键
     * @param obj 元素对象
     * @return
     *&#x2F;
    public boolean isMember(String key, Object obj) &#123;
        return redisTemplate.opsForSet().isMember(key, obj);
    &#125;

    &#x2F;**
     * 转移变量的元素值到目的变量。
     *
     * @param key     键
     * @param value   元素对象
     * @param destKey 元素对象
     * @return
     *&#x2F;
    public boolean move(String key, String value, String destKey) &#123;
        return redisTemplate.opsForSet().move(key, value, destKey);
    &#125;

    &#x2F;**
     * 批量移除set缓存中元素
     *
     * @param key    键
     * @param values 值
     * @return
     *&#x2F;
    public void remove(String key, Object... values) &#123;
        redisTemplate.opsForSet().remove(key, values);
    &#125;

    &#x2F;**
     * 通过给定的key求2个set变量的差值
     *
     * @param key     键
     * @param destKey 键
     * @return
     *&#x2F;
    public Set&lt;Set&gt; difference(String key, String destKey) &#123;
        return redisTemplate.opsForSet().difference(key, destKey);
    &#125;


    &#x2F;&#x2F;- - - - - - - - - - - - - - - - - - - - -  hash类型 - - - - - - - - - - - - - - - - - - - -

    &#x2F;**
     * 加入缓存
     *
